	public function __construct(Customweb_Payment_Authorization_IPaymentMethod $paymentMethod, Customweb_Datatrans_Configuration $config){
		parent::__construct($paymentMethod);
		$this->globalConfiguration = $config;
		$this->logger = Customweb_Core_Logger_Factory::getLogger(get_class());
	}

	public function updateTransactionState(Customweb_Datatrans_Authorization_Transaction $transaction){}

