	 *
	 * @param Customweb_Payment_Authorization_IOrderContext $orderContext
	 * @param Customweb_Payment_Authorization_IPaymentCustomerContext $paymentContext
	 * @param array $formData
	 * @throws Exception Throws Exception in case something is invalid
	 * @return void
	 */
	public function validate(Customweb_Payment_Authorization_IOrderContext $orderContext, Customweb_Payment_Authorization_IPaymentCustomerContext $paymentContext, array $formData){
		return;
	}

	/**
	 *       	    	 			  	   
	 *
	 * @return Customweb_Datatrans_Configuration
	 */
	protected function getGlobalConfiguration(){
		return $this->globalConfiguration;
	}

	/**
	 * (non-PHPdoc)
	 *
	 * @see Customweb_Payment_Authorization_AbstractPaymentMethodWrapper::getPaymentInformationMap()
	 */
	protected function getPaymentInformationMap(){
		return self::$paymentMapping;
	}

	public function getIframeHeight(){
		return $this->getPaymentMethodConfigurationValue('iframe_height');
	}

	public function getRememberMe(){
		if ($this->existsPaymentMethodConfigurationValue('remember_me')) {
			return $this->getPaymentMethodConfigurationValue('remember_me');
		}
		return 'inactive';
	}

	/**
	 * This method returns a list of form elements.
	 * This form elements are used to generate the user input.
	 * Sub classes may override this method to provide their own form fields.
	 *
	 * @return array List of form elements
	 */
	public function getFormFields(Customweb_Payment_Authorization_IOrderContext $orderContext, $aliasTransaction, $failedTransaction, $authorizationMethod, $isMoto, $customerPaymentContext){
		return array();
	}

	/**
	 * This method returns the parameters to add for processing an authorization request for this payment method.
	 * Sub classes
	 * may override this method. But they should call the parent and merge in their own parameters.
	 *
	 * @param Customweb_Datatrans_Authorization_Transaction $transaction
	 * @param array $formData
	 * @return array
	 */
	public function getAuthorizationParameters(Customweb_Datatrans_Authorization_Transaction $transaction, array $formData){
		$parameters = $this->getCustomerParameters($transaction);
		$paymentMethod = $this->getPaymentMethodType();
		if (!empty($paymentMethod)) {
			if ($transaction->getAuthorizationMethod() == Customweb_Payment_Authorization_Server_IAdapter::AUTHORIZATION_METHOD_NAME ||
					 $transaction->getAuthorizationMethod() == Customweb_Payment_Authorization_Recurring_IAdapter::AUTHORIZATION_METHOD_NAME) {
				$parameters['pmethod'] = $paymentMethod;
			}
			else {
				$parameters['paymentmethod'] = $paymentMethod;
			}
		}
		return $parameters;
	}

	public function getRecurringAuthorizationParameters(Customweb_Datatrans_Authorization_Transaction $transaction){
		$parameters = array();
		if ($transaction->getInitialTransaction() !== null) {
			$parameters['aliasCC'] = $transaction->getInitialTransaction()->getAliasToken();
		}
		$parameters['pmethod'] = $this->getPaymentMethodType();
		return $parameters;
	}

	public function getPaymentMethodType(){
		$params = $this->getPaymentMethodParameters();
		if (isset($params['PaymentMethod'])) {
			return $params['PaymentMethod'];
		}
		else {
			return NULL;
		}
	}

	/**
	 * This method returns the brand given by the pmethod.
	 *
	 * @param string $pmethod
	 * @return null|string
	 */
	public function mapPmethodToBrand($pmethod){
		$pmethod = strtolower($pmethod);
		foreach ($this->getPaymentInformationMap() as $brand => $methodData) {
			if (isset($methodData['parameters']['PaymentMethod']) && strtolower($methodData['parameters']['PaymentMethod']) == $pmethod) {
				return $brand;
			}
		}
		
		return null;
	}

	/**
	 * This method maps a given brand to pmethod.
	 *
	 * @param string $brand
	 * @return null|array
	 */
	public function getPaymentInformationByBrand($brand){
		$map = $this->getPaymentInformationMap();
		$brand = strtolower($brand);
		if (isset($map[$brand])) {
			return $map[$brand];
		}
		else {
			return null;
		}
	}

	/**
	 * Create the error for a declined transaction. Can be overwritten to e.g. use acqErrorCode instead of errorMessage
	 * 
	 * @param array $parameters
	 * @return Customweb_Payment_Authorization_ErrorMessage
	 */
	public function getFailedMessage(array $parameters){
		if (isset($parameters['errorMessage'])) {
			$failedMessage = Customweb_I18n_Translation::__("Payment Error Message: !message",
					array(
						'!message' => $parameters['errorMessage'] 
					));
			if (isset($parameters['errorDetail'])) {
				$failedMessage .= ' (' . $parameters['errorDetail'] . ')';
			}
			return new Customweb_Payment_Authorization_ErrorMessage(Customweb_I18n_Translation::__("The transaction was declined."), $failedMessage);
		}
		new Customweb_Payment_Authorization_ErrorMessage(Customweb_I18n_Translation::__("The transaction was declined."), Customweb_I18n_Translation::__("The transaction failed with an unkown error."));
	}

	protected function getCustomerParameters(Customweb_Datatrans_Authorization_Transaction $transaction){
		$sendCustomerAddress = $transaction->getTransactionContext()->getOrderContext()->getPaymentMethod()->getPaymentMethodConfigurationValue(
				'address_mode');
		
		$parameters = array();
		if (strtolower($sendCustomerAddress) == 'billing') {
			$parameters = $this->getBillingAddressParameters($transaction->getTransactionContext()->getOrderContext());
		}
		else if (strtolower($sendCustomerAddress) == 'delivery') {
			$parameters = $this->getShippingAddressParameters($transaction->getTransactionContext()->getOrderContext());
		}
		
		if (count($parameters) > 0) {
			$parameters['uppCustomerDetails'] = 'yes';
		}
		
		return $parameters;
	}

	protected function getBillingAddressParameters(Customweb_Payment_Authorization_IOrderContext $orderContext){
		$parameters = array();
		
		$parameters['uppCustomerName'] = Customweb_Util_String::substrUtf8(
				$orderContext->getBillingFirstName() . ' ' . $orderContext->getBillingLastName(), 0, 40);
		$parameters['uppCustomerFirstName'] = Customweb_Util_String::substrUtf8($orderContext->getBillingFirstName(), 0, 40);
		$parameters['uppCustomerLastName'] = Customweb_Util_String::substrUtf8($orderContext->getBillingLastName(), 0, 40);
		$parameters['uppCustomerStreet'] = Customweb_Util_String::substrUtf8($orderContext->getBillingStreet(), 0, 40);
		$parameters['uppCustomerCity'] = Customweb_Util_String::substrUtf8($orderContext->getBillingCity(), 0, 40);
		$parameters['uppCustomerCountry'] = $orderContext->getBillingCountryIsoCode();
		$parameters['uppCustomerZipCode'] = $orderContext->getBillingPostCode();
		$parameters['uppCustomerEmail'] = Customweb_Util_String::substrUtf8($orderContext->getCustomerEMailAddress(), 0, 40);
		$birthday = $orderContext->getBillingDateOfBirth();
		if ($birthday !== NULL && !empty($birthday)) {
			$parameters['uppCustomerBirthDate'] = $birthday->format('Y-m-d');
		}
		
		if (strtolower($orderContext->getBillingGender()) == 'company' || $orderContext->getBillingAddress()->getCompanyName() != null) {
			$parameters['uppCustomerType'] = 'C';
			$parameters['uppCustomerName'] = $orderContext->getBillingAddress()->getCompanyName();
		}
		else if (strtolower($orderContext->getBillingGender()) == 'male') {
			$parameters['uppCustomerGender'] = 'male';
			$parameters['uppCustomerType'] = 'P';
		}
		else if (strtolower($orderContext->getBillingGender()) == 'female') {
			$parameters['uppCustomerGender'] = 'female';
			$parameters['uppCustomerType'] = 'P';
		}
		
		return $parameters;
	}

	protected function getShippingAddressParameters(Customweb_Payment_Authorization_IOrderContext $orderContext){
		$parameters = array();
		
		$parameters['uppCustomerName'] = Customweb_Util_String::substrUtf8(
				$orderContext->getShippingFirstName() . ' ' . $orderContext->getShippingLastName(), 0, 40);
		$parameters['uppCustomerFirstName'] = Customweb_Util_String::substrUtf8($orderContext->getShippingFirstName(), 0, 40);
		$parameters['uppCustomerLastName'] = Customweb_Util_String::substrUtf8($orderContext->getShippingLastName(), 0, 40);
		$parameters['uppCustomerStreet'] = Customweb_Util_String::substrUtf8($orderContext->getShippingStreet(), 0, 40);
		$parameters['uppCustomerCity'] = Customweb_Util_String::substrUtf8($orderContext->getShippingCity(), 0, 40);
		$parameters['uppCustomerCountry'] = $orderContext->getShippingCountryIsoCode();
		$parameters['uppCustomerZipCode'] = $orderContext->getShippingPostCode();
		$parameters['uppCustomerEmail'] = Customweb_Util_String::substrUtf8($orderContext->getCustomerEMailAddress(), 0, 40);
		$birthday = $orderContext->getShippingDateOfBirth();
		if ($birthday !== NULL && !empty($birthday)) {
			$parameters['uppCustomerBirthDate'] = $birthday->format('Y-m-d');
		}
		
		if (strtolower($orderContext->getShippingGender()) == 'company' || $orderContext->getShippingAddress()->getCompanyName() != null) {
			$parameters['uppCustomerType'] = 'C';
			$parameters['uppCustomerName'] = $orderContext->getShippingAddress()->getCompanyName();
		}
		else if (strtolower($orderContext->getShippingGender()) == 'male') {
			$parameters['uppCustomerGender'] = 'male';
			$parameters['uppCustomerType'] = 'P';
		}
		else if (strtolower($orderContext->getShippingGender()) == 'female') {
			$parameters['uppCustomerGender'] = 'female';
			$parameters['uppCustomerType'] = 'P';
		}
		return $parameters;
	}
